css:
.advice-container {
    font-family: Arial, sans-serif;
    font-size: 12px;
    padding: 10px;
}
.header-center {
    text-align: center;
    font-weight: bold;
}
.address-line {
    text-align: center;
}
.info-table {
    width: 100%;
    margin: 10px 0;
}
.info-table td {
    padding: 3px 0;
}
.payee {
    margin-top: 20px;
    font-weight: bold;
}
.invoice-table {
    width: 100%;
    border-collapse: collapse;
    margin-top: 10px;
}
.invoice-table th,
.invoice-table td {
    border: 1px solid #000;
    padding: 6px;
    text-align: left;
}
.invoice-table th {
    background-color: #f9f9f9;
    font-weight: bold;
    text-align: center;
}
.invoice-table tfoot td {
    font-weight: bold;
    text-align: center;
}
.footer-msg {
    margin-top: 30px;
}
.signature {
    margin-top: 50px;
    text-align: left;
}
