css:
    .print-format {
        font-family: Arial, sans-serif;
        font-size: 12px;
        color: #000;
        width: 100%;
        padding: 30px;
    }
    .center {
        text-align: center;
    }
    .header {
        text-align: center;
    }
     .bold {
        font-weight: bold;
    }
    .spacer {
        height: 15px;
    }   

    .header h2, .header h4 {
        margin: 2px 0;
    }

    .meta-table, .account-table, .signature-table {
        width: 100%;
        border-collapse: collapse;
        margin-top: 10px;
        color: #000;
        font-size: 14px;
    }
    .print-format th{
        background-color: #ffffff !important;
    }
    .meta-table td {
        padding: 4px 6px;
        vertical-align: top;
    }

    .account-table th, .account-table td {
        padding: 6px 4px;
    }

    .account-table th {
        border-top: 1px solid #000;
        border-bottom: 1px solid #000;
        text-align: left;
    }



    .group-label {
        font-weight: bold;
        padding-top: 8px;
    }

    .signature-table td {
        padding-top: 40px;
        text-align: center;
    }
    .section-title {
        font-weight: bold;
        font-size: 16px;
        margin-top: 10px;
    }

    .no-border {
        border: none !important;
    }

    .amount-words {
        font-weight: bold;
        margin-top: 10px;
    }

    .right {
        text-align: right;
    }

    .sub-label {
        font-size: 11px;
        color: #333;
    }





    .account-table {
    width: 100%;
    border-collapse: collapse;
    font-size: 12px;
}

.account-table th,
.account-table td {
    padding: 6px 4px;
    vertical-align: top;
}

.group-label {
    font-weight: bold;
    padding-top: 10px;
}

.right {
    text-align: right;
}
