css:
.voucher-container {
    font-family: Arial, sans-serif;
    font-size: 14px;
    padding: 10px;
}

.center-text {
    text-align: center;
}

.bold {
    font-weight: bold;
}

.voucher-header {
    margin-bottom: 5px;
    text-align: center;
}

.info-table, .account-table {
    width: 100%;
    border-collapse: collapse;
}

.info-table td {
    padding: 4px 0;
}

.account-table th {
    border-bottom: 1px solid black;
    /*text-align: left;*/
    padding: 6px;
}

.account-table td {
    padding: 6px;
    vertical-align: top;
}

.amount-col {
    border-left: 1px solid black;
    text-align: right;
    width: 30%;
}

.particulars-col {
    width: 70%;
}

.total-row {
    /*border-top: 1px solid black;*/
    font-weight: bold;
}

.section-label {
    font-weight: bold;
    padding-top: 8px;
}

.signature-section td {
    padding-top: 30px;
    text-align: center;
}

.receiver-sign {
    padding-top: 50px;
}

.amount-in-words {
    white-space: pre-wrap;
}
