css:
.print-format {
  font-family: Arial, sans-serif;
  font-size: 12px;
  padding: 10px;
  color: #000;
}
.header-title {
  font-size: 14px;
  font-weight: bold;
  color: #999;
  margin-bottom: 10px;
}
.company-info, .buyer-info, .order-info {
  width: 100%;
  border-collapse: collapse;
}
.company-info td,
.buyer-info td,
.order-info td {
  vertical-align: top;
  padding: 4px 8px;
}
.label {
  font-weight: bold;
  border: none !important;
}
.section-title {
  font-weight: bold;
  margin-top: 10px;
}
.border-top {
  border-top: 1px solid #aaa;
  margin-top: 10px;
}

    table {
      width: 100%;
      border-collapse: collapse;
      font-size: 12px;
  }

  th, td {
      padding: 6px 8px;
      text-align: left;
      vertical-align: top;
  }

  .no-border td,
  .no-border th {
      border: none !important;
  }

  .line-table td, .line-table th {
      border: 1px solid #ddd;
  }

  .section-title {
      font-weight: bold;
      padding-top: 10px;
      font-size: 13px;
  }

  .summary-table td {
      padding: 4px 6px;
  }

  .right {
      text-align: right;
  }

  .bold {
      font-weight: bold;
  }

  .no-border-top td {
      border-top: none !important;
  }
