css:
.print-container {
    font-family: Arial, sans-serif;
    font-size: 14px;
    width: 100%;
    padding: 10px 20px;
}

.text-center {
    text-align: center;
}

.text-right {
    text-align: right;
}

.text-left {
    text-align: left;
}

.bold {
    font-weight: bold;
}

.header-table, .details-table, .footer-table {
    width: 100%;
    border-collapse: collapse;
    margin-bottom: 10px;
}

.spacer {
    height: 15px;
} 

.details-table td {
    padding: 6px 5px;
    vertical-align: top;
}

.footer-table td {
    padding-top: 20px;
}

hr {
    border: 0;
    border-top: 1px solid #000;
}
