.receipt-table {
    width: 100%;
    font-family: Arial, sans-serif;
    font-size: 12px;
    border-collapse: collapse;
}

.receipt-table th {
    border-bottom: 1px solid black;
    /*text-align: left;*/
    padding: 6px;
}

.receipt-table td {
    padding: 6px;
    vertical-align: top;
    align-content: center;
}

.amount-col {
    border-left: 1px solid black;
    text-align: right;
    width: 30%;
}

.particulars-col {
    width: 70%;
}

.bold {
    font-weight: bold;
}

.total-row {
    border-top: 1px solid black;
    font-weight: bold;
}

.section-label {
    font-weight: bold;
    padding-top: 10px;
}

.amount-in-words, .on-account, .through, .accounts {
    white-space: pre-wrap;
}
