css:
  .invoice-print {
      font-family: Arial, sans-serif;
      font-size: 14px;
      color: #000;
      width: 100%;
  }

  .invoice-header {
      text-align: center;
      margin-bottom: 10px;
  }

  .invoice-header h2 {
      margin: 0;
      font-size: 16px;
      font-weight: bold;
  }

  .invoice-header p {
      margin: 2px 0;
      font-size: 14px;
  }

  .section {
      margin: 10px 0;
  }

  .table-no-border{
      width: 100%;
      border-collapse: collapse;
  }
  .table-no-border, .table-no-border td, .table-no-border th {
      border: none !important;
      padding: 4px 6px;
  }

/*.print-format {*/
/*  font-family: Arial, sans-serif;*/
/*  font-size: 12px;*/
/*  padding: 10px;*/
/*}*/
.table {
  width: 100%;
  border-collapse: collapse;
  margin-top: 10px;
}
.table td {
  padding: 6px 10px;
  vertical-align: top;
}
.table-bordered td {
  border: 1px solid #ddd;
}
.border-top{
    border:1px solid #000;
}
.right {
  text-align: right;
}
.bold {
  font-weight: bold;
}

.border-none td {
  border: none !important;
}
.amount-section {
  margin-top: 15px;
}
.amount-in-words {
  margin-top: 10px;
  font-style: italic;
}
