css:
body {
    font-family: Arial, sans-serif;
    font-size: 14px;
    color: #000;
}

table {
    width: 100%;
    border-collapse: collapse;
}

td, th {
    padding: 4px 6px;
    vertical-align: top;
}

.no-border td, .no-border th {
    border: none !important;
    font-size:14px;
}

.bold {
    font-weight: bold;
}

.right {
    text-align: right;
}

.center {
    text-align: center;
}

.small {
    font-size: 11px;
}

.top-border {
    border-top: 1px solid #000;
}

.bottom-border {
    border-bottom: 1px solid #000;
}

.box {
    border: 1px solid #ccc;
    padding: 6px;
    margin-top: 5px;
}

.section-title {
    font-weight: bold;
    font-size: 14px;
    margin-top: 10px;
}

.spacer {
    height: 15px;
}
