css:
.print-format{
    margin:0;
    padding:0;
}

.print-heading {
	text-align: center;
    text-transform: none;
    color: black;
    border-bottom: 1px solid #ccc;
    padding-bottom:10px !important;
    border-top: 1px solid #ccc;
    padding-top:10px !important;
    margin-bottom: 1px;
    font-weight:bold;
}

table{
    line-height:1;
    font-size:9px;
    width:100%;
    table-layout: fixed;
    page-break-inside: avoid;
}

.border-1 {
    border:1px solid black;
    /*margin-top:15px;*/
}
.print-format{
    margin-left:2mm;
    margin-right:2mm;
}
.border-2 {
    border-right:1px solid black;
    height: 10px;
}
/*td[colspan="4"] {*/
/*    width: 50%;*/
/*}*/

/*td[colspan="3"] {*/
/*    width: 50%;*/
/*}*/

.right{
    text-align:right !important;
}
.height{
    height:25px !important;
}
.center{
    text-align:center !important;
}
.b{
    font-weight: bold !important;
}
.col1 {
    width: 25%;
}

.col2 {
    width: 10%;
}

.col3 {
    width: 10%;
}

.col4 {
    width: 10%;
}

.col5 {
    width: 25%;
}

.col6 {
    width: 10%;
}

.col7 {
    width: 10%;
}

.print-format td, .print-format th {
    vertical-align: top !important;
    padding: 2px !important;
    padding-left: 2px !important;
}

h3, .h3 {
    font-size: 14px !important;
    margin-top: 0px !important;
    margin-bottom: 0px !important;
}
h2, .h2 {
    margin-top: 0px !important;
    margin-bottom: 0px !important;
}

@media print {
    h3, .h3 {
        margin-top: 0px !important;
        padding-top: 0px !important;
    }
}
 div[document-status="draft"]{
     display:none;
     padding:none;
     margin:none;
 }
.col2-1{
    width:35%;
}
.col2-2{
    width:10%;
}
.seal {
    float: right;
    width: 80px;
    margin-top: -50px;
}

body:last-child .print-format td img {
     width: 40% !important; 
}

