css:
.invoice-box {
    font-family: Arial, sans-serif;
    font-size: 14px;
    color: #000;
    width: 100%;
}

.invoice-box table {
    width: 100%;
    border-collapse: collapse;
}

.invoice-box td, .invoice-box th {
    padding: 3px;
    vertical-align: top;
}

.bordered td, .bordered th {
    border: 1px solid #000;
}

.no-border td {
    border: none;
}

.bold {
    font-weight: bold;
}

.center {
    text-align: center;
}

.right {
    text-align: right;
}

.title {
    font-size: 16px;
    font-weight: bold;
    text-align: right;
}

.header-box {
    border: 1px solid black;
    padding: 5px;
    margin-bottom: 5px;
}

.uppercase {
    text-transform: uppercase;
}


 table.tax-breakdown {
    width: 100%;
    border-collapse: collapse;
    font-size: 12px;
}

table.tax-breakdown th, table.tax-breakdown td {
    padding: 6px;
    text-align: center;
    vertical-align: middle;
}

table.tax-breakdown th {
    font-weight: bold;
}
.tax-breakdown th{
    background-color: #fff !important;
}

.tax-breakdown tr th{
    background-color: #fff !important;
}

.border-bottom {
    border-bottom: 1px solid #333;
}

.border-top {
    border-top: 1px solid #333;
}

.right {
    text-align: right;
}

.bold {
    font-weight: bold;
}
